INSERT INTO game(title, `description`, cover, price) VALUES ("Street Fighter 6", "Voici le tout nouveau challenger de Capcom ! Street Fighter™ 6, le dernier opus de la série, sera disponible mondialement le 2 juin 2023 ! L'expérience Street Fighter 6 s'étend sur trois modes de jeu distincts : World Tour, Fighting Ground et Battle Hub.", "sf6.jpg", "59,99€");
INSERT INTO game(title, `description`, cover, price) VALUES ("Apex Legends", "Apex Legends est le célèbre jeu de tir gratuit avec des héros créé par Respawn Entertainment. Maîtrisez une équipe de personnages légendaires dotés de capacités surpuissantes, et découvrez une jouabilité novatrice d'une grande profondeur tactique dans cette nouvelle évolution du genre.", "apexlegends.jpg", "Gratuit");
INSERT INTO game(title, `description`, cover, price) VALUES ("Forza Horizon 5", "Explorez un monde ouvert plein de vie dans les paysages du Mexique et vibrez au volant de voitures d'exception. Partez à la conquête des pistes accidentées de la Sierra Nueva dans cette expérience ultime du Rallye Horizon. Nécessite le jeu Forza Horizon 5, extension vendue séparément.", "forzahorizon5.jpg", "29,99€");
INSERT INTO game(title, `description`, cover, price) VALUES ("Baldur's Gate 3", "Constituez votre groupe et retournez aux Royaumes Oubliés dans une histoire d'amitié, de trahison, de sacrifice et de survie, sur fond d'attrait du pouvoir absolu.", "bg3.jpg", "59,99€");
INSERT INTO game(title, `description`, cover, price) VALUES ("Brotato", "Brotato est un roguelite où vous incarnez une pomme de terre maniant jusqu'à 6 armes à la fois pour combattre des hordes d'extraterrestres. Choisissez parmi une variété de traits et d'objets pour créer des parties uniques et survivre jusqu'à l'arrivée des secours.", "brotato.jpg", "4,99€");
INSERT INTO game(title, `description`, cover, price) VALUES ("Amnesia: The Bunker", "Amnesia: The Bunker est un jeu d'horreur à la première personne qui se déroule dans un bunker de la Première Guerre mondiale. Faites face aux dangers tapis dans l'ombre. Trouvez les outils et les armes à votre disposition, et gardez les lampes allumées pour espérer survivre.", "amnesia.jpg", "24,50€");
INSERT INTO game(title, `description`, cover, price) VALUES ("F1® 23", "Dépassez le point de rupture dans EA SPORTS™ F1® 23, le jeu vidéo officiel du 2023 FIA Formula One World Championship™. Retrouvez un nouveau chapitre du mode histoire « Point de rupture » palpitant, avec ses drames à grande vitesse et ses rivalités enflammées.", "f123.jpg", "41,99€");
INSERT INTO game(title, `description`, cover, price) VALUES ("PUBG: BATTLEGROUNDS", "Jouez gratuitement à PUBG: BATTLEGROUNDS. Atterrissez à des emplacements stratégiques, pillez des armes et du ravitaillement, puis survivez pour devenir la dernière équipe en lice lors de combats farouches sur des champs de bataille tout aussi divers que variés.", "pubg.jpg", "Gratuit");
